sys_script_client.script:
function onChange(control, oldValue, newValue) {
    //console.log("Visa Copy field value:", newValue);

    if (!newValue || typeof newValue !== 'string') {
        g_form.showFieldMsg('visa_copy', 'Visa Copy is mandatory and must be a PDF.', 'error');
        return;
    }

    // Normalize value and check if it includes ".pdf" (case-insensitive)
    var cleanedFileName = newValue.trim().toLowerCase();

    // Accept file if ".pdf" appears anywhere in the filename and it's not just a trick
    if (cleanedFileName.includes('.pdf')) {
        g_form.hideFieldMsg('visa_copy', true);
    } else {
        g_form.showFieldMsg('visa_copy', 'Only PDF format is allowed for Visa Copy.', 'error');
        g_form.setValue('visa_copy', ''); // Optional: clears the invalid file
    }
}
