sys_script_client.script:
function onChange(control, oldValue, newValue) {
    if (!newValue) {
        alert("Employee ID is required.");
        return;
    }

    var ga = new GlideAjax('x_1791348_travel_0.getEmpDet');
    ga.addParam('sysparm_name', 'getEmpInfo');
    ga.addParam('sysparm_empnum', newValue);

	//alert(newValue);

    ga.getXMLAnswer(function(response) {
        var data = JSON.parse(response || '{}');

        if (data) {
            g_form.setValue('name', data.name);
            g_form.setValue('email', data.email);
            g_form.setValue('contact_number', data.contact_number);
            g_form.setValue('project', data.project);
            g_form.setValue('tdm_or_account_manager', data.tdm_or_account_manager);
            g_form.setValue('card_number', data.card_number);
            g_form.setValue('bank_name_of_the_card', data.bank_name_of_the_card);
        } else {
            alert("No data found for the given Employee ID.");
        }
    });
}
