sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var dur_to = new Date(g_form.getValue('duration_to'));
   var dur_from = new Date(g_form.getValue('duration_from'));
   var today = new Date();

   // Check if To Date is before From Date
   if (dur_to < dur_from) {
      g_form.showFieldMsg('duration_to', 'Duration To Date cannot be before Duration From Date.', 'error');
      return;
   }

   // Check if To Date is in the past
   if (dur_to < today) {
      g_form.showFieldMsg('duration_to', 'Duration To Date cannot be in the past.', 'error');
      return;
   }
}
